import React, { useState } from 'react';

const Create = (props)=>{
  const[note , newNote] = useState({
      title: " ",
      content: " "
  } );

  const onEvent = (main) =>{
    //   newNote(main.target.value);
      const{name , value} = main.target;
      newNote((preVal)=>{
         
        return{ 
            ...preVal,
            [name]: value
              
        }
          
      })
    } 
     const onSubmit = () => {
     newNote(props.fatch.note)
    }
    return(<>
        <div className = "create_note">
            <form>
            <input type = "text" 
            placeholder = "File Title*"
            onChange={onEvent}
            name ="title"
            value={note.title}
            required/>
            <br/>
            <textarea type= "text"
             rows = "8"
             cols= "40"
             placeholder ="Write your Credential"
             onChange={onEvent}
             name="content"
             value= {note.content}
             required/>
            <button onClick = {onSubmit}>+</button>
            </form>
        </div>

    </>)
}
export default Create;
